import React from 'react';
import { Box, CssBaseline, useTheme } from '@mui/material';
import Navbar from '../components/Navbar';
import Sidebar from '../components/Sidebar';

const drawerWidth = 240; // Pastikan lebar sidebar konsisten

function DashboardLayout({ children }) {
  const theme = useTheme();

  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      <Navbar />
      <Sidebar />
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 3, // Padding menggunakan sistem spacing
          width: { sm: `calc(100% - ${drawerWidth}px)` }, // Gunakan drawerWidth variabel
          marginTop: '64px',
          marginLeft: { sm: `${drawerWidth}px` }, // Tambahkan margin-left sebesar lebar sidebar
          boxSizing: 'border-box',
        }}
      >
        {children}
      </Box>
    </Box>
  );
}

export default DashboardLayout;
